// Get all subscription items from the input
const subscriptions = $input.all();

// Get today's date
const today = new Date();
today.setHours(0, 0, 0, 0);

// Function to calculate next payment date based on last payment date and Type
function calculateNextPaymentDate(lastPaymentDate, subscriptionType) {
  if (!lastPaymentDate) return null;

  const lastDate = new Date(lastPaymentDate);
  const nextDate = new Date(lastDate);

  if (subscriptionType && subscriptionType.toLowerCase() === 'monthly') {
    // Add 1 month
    nextDate.setMonth(nextDate.getMonth() + 1);
  } else if (subscriptionType && (subscriptionType.toLowerCase() === 'annually' || subscriptionType.toLowerCase() === 'annual')) {
    // Add 1 year
    nextDate.setFullYear(nextDate.getFullYear() + 1);
  } else {
    // Default to monthly if not specified
    nextDate.setMonth(nextDate.getMonth() + 1);
  }

  // Format as YYYY-MM-DD
  return nextDate.toISOString().split('T')[0];
}

// Process each subscription
const updatedSubscriptions = subscriptions.map(item => {
  const sub = item.json;

  // Calculate next payment date based on last_payment_date
  const nextPaymentDate = calculateNextPaymentDate(
    sub.last_payment_date,
    sub.Type
  );

  // Check if today equals or is past the next payment date
  const nextPayment = new Date(sub.Next_payment);
  nextPayment.setHours(0, 0, 0, 0);

  let updatedLastPaymentDate = sub.last_payment_date;

  // If today is on or after the next payment date, update last_payment_date to next_payment
  if (sub.Next_payment && today >= nextPayment) {
    updatedLastPaymentDate = sub.Next_payment;
  }

  return {
    json: {
      ...sub,
      calculated_next_payment: nextPaymentDate,
      updated_last_payment_date: updatedLastPaymentDate
    }
  };
});

return updatedSubscriptions;

// Get all subscription items from the input
const subscriptions = $input.all();

// Get today's date
const today = new Date();
today.setHours(0, 0, 0, 0);

// Calculate date 14 days from now
const twoWeeks = new Date(today);
twoWeeks.setDate(twoWeeks.getDate() + 14);

// Array to store annual renewals
const annualRenewals = [];

// Check each subscription
for (const item of subscriptions) {
  const sub = item.json;

  // Check if it's an annual subscription using Type field
  if (sub.Type && (sub.Type.toLowerCase() === 'annually' || sub.Type.toLowerCase() === 'annual')) {

    // Check next_payment_date from Google Sheet
    if (sub.next_payment_date) {
      const paymentDate = new Date(sub.next_payment_date);
      paymentDate.setHours(0, 0, 0, 0);

      // Check if payment is within next 14 days
      if (paymentDate >= today && paymentDate <= twoWeeks) {
        const daysUntil = Math.ceil((paymentDate - today) / (1000 * 60 * 60 * 24));

        annualRenewals.push({
          ...sub,
          isUrgent: true,
          daysUntilRenewal: daysUntil,
          renewalDate: paymentDate.toLocaleDateString()
        });
      }
    }
  }
}

// Generate urgent HTML email if there are annual renewals
let urgentHtml = '';
if (annualRenewals.length > 0) {
  urgentHtml = '<html><body style="font-family: Arial, sans-serif;">';
  urgentHtml += '<div style="background-color: #ff4444; color: white; padding: 20px; border-radius: 5px; margin-bottom: 20px;">';
  urgentHtml += '<h2 style="margin: 0;">üö® URGENT: Annual Subscription Renewal Alert</h2>';
  urgentHtml += '</div>';

  urgentHtml += '<p style="font-size: 16px; color: #333;">You have <strong>' + annualRenewals.length + '</strong> annual subscription(s) renewing in the next 14 days:</p>';

  urgentHtml += '<table style="border-collapse: collapse; width: 100%; margin-bottom: 20px;">';
  urgentHtml += '<tr style="background-color: #ffcccc;">';
  urgentHtml += '<th style="border: 1px solid #ddd; padding: 12px; text-align: left;">Subscription</th>';
  urgentHtml += '<th style="border: 1px solid #ddd; padding: 12px; text-align: left;">Amount</th>';
  urgentHtml += '<th style="border: 1px solid #ddd; padding: 12px; text-align: left;">Renewal Date</th>';
  urgentHtml += '<th style="border: 1px solid #ddd; padding: 12px; text-align: left;">Days Until Renewal</th>';
  urgentHtml += '</tr>';

  for (const renewal of annualRenewals) {
    const urgencyColor = renewal.daysUntilRenewal <= 7 ? '#ff6666' : '#ffcccc';
    urgentHtml += '<tr style="background-color: ' + urgencyColor + ';">';
    urgentHtml += '<td style="border: 1px solid #ddd; padding: 12px;">' + renewal.Name + '</td>';
    urgentHtml += '<td style="border: 1px solid #ddd; padding: 12px;">' + renewal.Currency + ' ' + renewal.Amount + '</td>';
    urgentHtml += '<td style="border: 1px solid #ddd; padding: 12px;">' + renewal.renewalDate + '</td>';
    urgentHtml += '<td style="border: 1px solid #ddd; padding: 12px; font-weight: bold;">' + renewal.daysUntilRenewal + ' days</td>';
    urgentHtml += '</tr>';
  }
  urgentHtml += '</table>';

  urgentHtml += '<div style="background-color: #fff3cd; border-left: 4px solid #ffc107; padding: 15px; margin-top: 20px;">';
  urgentHtml += '<p style="margin: 0; font-weight: bold;">‚ö†Ô∏è Action Required:</p>';
  urgentHtml += '<p style="margin: 5px 0 0 0;">Please review these subscriptions and decide whether to renew or cancel before the renewal date.</p>';
  urgentHtml += '</div>';

  urgentHtml += '<hr style="margin: 20px 0;">';
  urgentHtml += '<p style="color: #666; font-size: 12px;">This is an automated urgent renewal alert.</p>';
  urgentHtml += '</body></html>';
}

// Get email from first subscription
const email = subscriptions[0]?.json?.email;

// Return all subscriptions with urgent flag and HTML added
const result = subscriptions.map(item => {
  const sub = item.json;
  const urgentRenewal = annualRenewals.find(r => r.Name === sub.Name);

  return {
    json: {
      ...sub,
      isUrgent: urgentRenewal ? true : false,
      daysUntilRenewal: urgentRenewal ? urgentRenewal.daysUntilRenewal : null,
      hasUrgentAnnualRenewals: annualRenewals.length > 0,
      urgentHtml: urgentHtml,
      email: email
    }
  };
});

return result;

