// Add next_payment_date field equal to calculated_next_payment for each item
const updatedSubscriptions = subscriptions.map(item => {
  const sub = item.json;

  return {
    json: {
      ...sub,
      next_payment_date: sub.calculated_next_payment
    }
  };
});

